catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    var ga = new GlideAjax('global.getLocation'); // getLocation is the script include class
    ga.addParam('sysparm_name', 'locationDetail'); //locationDetail is the script include method
    ga.addParam('user_sysid', g_form.getValue('requested_for')); // set parameter sysparm_user_sysid to 'requested_for'
    ga.getXML(setLocation); //Call getLocation.localtionDetail() with the parameter sysparm_user_sysid set to 'requested_for' and use the call back function setLocatioin to return the result when ready  
}

// Callback function for returning the result from the server-side code
function setLocation(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    g_form.setValue('office_location', answer);
}
